g=''
for x in'First^A Partridge in a Pear Tree.|Second^Two Turtle Doves, and|Third^Three French Hens,|Four#Four Calling Birds,|Fif#Five Gold Rings,|Six#Six Geese-a-Lay!Seven#Seven Swans-a-Swimm!Eigh#Eight Maids-a-Milk!Nin#Nine Ladies Danc!Ten#Ten Lords-a-Leap!Eleven#Eleven Pipers Pip!Twelf#Twelve Drummers Drumming,'.translate({35:'th^',33:'ing,|'}).split('|'):o,d=x.split('^');g=d+'\n'+g;print('On the',o,'day of Christmas\nMy true love sent to me\n'+g)
