g=''
for x in'First^A Partridge in a Pear Tree.|Second^Two Turtle Doves, and|Third^Three French Hens,|Fourth^Four Calling Birds,|Fifth^Five Gold Rings,|Sixth^Six Geese-a-Lay!Seventh^Seven Swans-a-Swimm!Eighth^Eight Maids-a-Milk!Ninth^Nine Ladies Danc!Tenth^Ten Lords-a-Leap!Eleventh^Eleven Pipers Pip!Twelfth^Twelve Drummers Drumming,'.replace('!','ing,|').split('|'):o,d=x.split('^');g=d+'\n'+g;print('On the',o,'day of Christmas\nMy true love sent to me\n'+g)
